mapper.select:
SELECT studno, s.name, s.userid, grade, idnum, DATE_FORMAT(birthdate, '%Y-%m-%d') AS birthdate, tel, height, weight, d.dname, CONCAT(P.name, '', P.position) AS pname FROM student s INNER JOIN department d ON s.deptno=d.deptno LEFT OUTER JOIN professor p ON s.profno=p.profno
s.name LIKE concat('%', #{name}, '%')
ORDER BY studno DESC LIMIT #{limitStart}, #{listCount}

SELECT studno, s.name, s.userid, grade, idnum, DATE_FORMAT(birthdate, '%Y-%m-%d') AS birthdate, tel,
 height, weight, d.dname, CONCAT(P.name, '', P.position) AS pname FROM student s
  INNER JOIN department d ON s.deptno=d.deptno LEFT OUTER JOIN professor p ON s.profno=p.profno WHERE studno=#{studno}
